mapper.select:
SELECT SU.USER_NO AS userNo ,SU.USER_ID AS userId ,SU.USER_NAME AS userName ,STUD.SCHOOL_CODE AS schoolCode ,STUD.CLASS_CODE AS classCode ,SCI.CLASS_NAME AS className FROM ST_USER SU LEFT OUTER JOIN ST_TEACHER_USER_DETAIL STUD ON SU.USER_ID = STUD.USER_ID RIGHT OUTER JOIN ( SELECT DISTINCT * FROM ST_CLASS_INFO WHERE 1=1 ) SCI ON STUD.CLASS_CODE = SCI.CLASS_CODE WHERE 1=1
AND STUD.SCHOOL_CODE = #{schoolCode}
AND SU.USER_TYPE = 'TC' ORDER BY SU.USER_ID ASC

mapper.update:

UPDATE ST_SCHOOL_INFO
SET
	USER_SEQ_NO  = #{userSeqNo}
WHERE 1=1
AND SCHOOL_CODE= #{schoolCode}
