mapper.update:

UPDATE ST_TEETH_MEASURE
SET  
	   DIAG_DESCRIPT = #{diagDescript}
WHERE 1=1
AND USER_ID = #{userId}
AND DATE_FORMAT(MEASURE_DT, '%Y-%m-%d') = #{measureDt}


UPDATE ST_TEETH_MEASURE
SET  DETERIORATE_SCORE  =  #{deteriorateScore}
WHERE 1=1
AND USER_ID = #{userId}
AND DATE_FORMAT(MEASURE_DT, '%Y-%m-%d') = #{measureDt}


UPDATE ST_TEETH_MEASURE
SET  
	   DIAG_DESCRIPT_FL = 'Y'
WHERE 1=1
AND USER_ID = #{userId}
AND DATE_FORMAT(MEASURE_DT, '%Y-%m-%d') = #{measureDt}


UPDATE ST_TEETH_MEASURE
SET  
	   MEMO = #{memo}
WHERE 1=1
AND USER_ID = #{userId}
AND DATE_FORMAT(MEASURE_DT, '%Y-%m-%d') = #{measureDt}
