mapper.delete:
DELETE FROM ST_USER
WHERE 1=1 
AND USER_ID=#{userId}

mapper.update:
UPDATE ST_USER
PUSH_TOKEN = #{pushToken},
WHERE 1=1 AND USER_ID = #{userId}

mapper.select:
SELECT SSUD.USER_ID AS USER_ID ,CAST(AES_DECRYPT(UNHEX(FROM_BASE64(SU.USER_NAME)), '${securityKey}' ) AS CHAR) AS USER_NAME ,CAST(AES_DECRYPT(UNHEX(FROM_BASE64(SU.USER_BIRTHDAY)), '${securityKey}' ) AS CHAR) AS USER_BIRTHDAY ,CAST(AES_DECRYPT(UNHEX(FROM_BASE64(SU.USER_TEL_NO)), '${securityKey}') AS CHAR) AS USER_TEL_NO ,SU.USER_SEX AS USER_SEX ,DATE_FORMAT(SU.USER_RGST_DT, '%Y-%m-%d') AS USER_RGST_DT ,SSUD.SCHOOL_TYPE AS SCHOOL_TYPE ,SSUD.IS_MEASURING AS IS_MEASURING ,SSUD.PA_USER_NAME AS PA_USER_NAME ,SSUD.PA_USER_TEL_NO AS PA_USER_TEL_NO ,SU.TEETH_TYPE AS TEETH_TYPE ,SU.PUSH_TOKEN AS PUSH_TOKEN FROM ST_STUDENT_USER_DETAIL SSUD LEFT OUTER JOIN ST_USER SU ON SSUD.USER_ID = SU.USER_ID WHERE 1=1
AND SSUD.TEACHER_ID = #{userId}
AND SU.USER_NAME NOT LIKE CONCAT('%', '환자', '%') GROUP BY SU.USER_ID ORDER BY SU.USER_NAME ASC

mapper.update:

		UPDATE ST_USER
